    }
  render(){
    return(
      <div className="ToDoList">
        Add an item..
        <br/>
        <input
            type="text"
            placeholder="Type item here"
            value={this.state.newItem}
            onChange={e => this.updateInput("newItem", e.target.value)}
          />
        <button onClick={()=> this.addItem()}>
          Add
        </button>
      </div>
    );
  }
}
